#!/bin/sh

# make sure required directories exist
mkdir $HOME/.local/share/applications 2> /dev/null
mkdir $HOME/.local/share/icons 2> /dev/null

# write desktop file
printf "[Desktop Entry]\nName=QtFind File Search\nGenericName=File search\nComment=Search the file system\nKeywords=files;find;locate;lookup;search;\nCategories=Qt;Utility;\nExec=%s %s\nIcon=%s/.local/share/icons/qtfind.png\nTerminal=false\nType=Application\nMimeType=inode/directory;\n" $(which qtfind) "%f" $HOME > $HOME/.local/share/applications/QtFind.desktop

# app icon in base64 format
ICON=""

# decode and save the app's icon
echo -n $ICON | base64 -d > $HOME/.local/share/icons/qtfind.png

# done
echo "done!"

